import React from "react";
import { useState } from "react";
import "./Payment.css";
import img1 from "./img/chip.png";
import img2 from "./img/visa.png";

const Payment = () => {
  const [number, setNumber] = useState("");
  const [name, setName] = useState("");
  const [month, setMonth] = useState("");
  const [year, setYear] = useState("");
  const [cvv, setCvv] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`The Name Entered is: ${name}`);
    alert(`The Email Entered is: ${email}`);
    alert(`The FeedBack Entered is: ${fdbk}`);
    setName("");
    setEmail("");
    setFdbk("");
  };
  
  return (
    <div class="container">
      <div class="card-container">
        <div class="front">
          <div class="image">
            <img src={img1} alt="" />
            <img src={img2} alt="" />
          </div>
          <div class="card-number-box">
            {number.length === 0 ? "#### #### #### ####" : number}
          </div>
          <div class="flexbox">
            <div class="box">
              <span>card holder</span>
              <div class="card-holder-name">
                {name.length === 0 ? "full name" : name}
              </div>
            </div>
            <div class="box">
              <span>expires</span>
              <div class="expiration">
                <span class="exp-month" style={{ marginRight: "10px" }}>
                  {month.length === 0 ? "mm" : month}
                </span>
                <span class="exp-year">{year.length === 0 ? "yy" : year}</span>
              </div>
            </div>
          </div>
        </div>

        <div class="back">
          <div class="stripe"></div>
          <div class="box">
            <span>{cvv.length === 0 ? "cvv" : cvv}</span>
            <div class="cvv-box"></div>
            <img src={img2} alt="" />
          </div>
        </div>
      </div>

      <form >
        <div class="inputBox">
          <span>card number</span>
          <input
            type="text"
            name="number"
            maxlength="16"
            class="card-number-input"
            required
            value={number}
            onChange={(x) => {
              console.log(x.target.value);
              return setNumber(x.target.value);
            }}
          />
        </div>
        <div class="inputBox">
          <span>card holder</span>
          <input
            type="text"
            name="holder"
            class="card-holder-input"
            required
            value={name}
            onChange={(x) => {
              console.log(x.target.value);
              return setName(x.target.value);
            }}
          />
        </div>
        <div class="flexbox">
          <div class="inputBox">
            <span>expiration mm</span>
            <select
              name="expmon"
              id=""
              class="month-input"
              required
              value={month}
              onChange={(x) => {
                console.log(x.target.value);
                return setMonth(x.target.value);
              }}
            >
              <option value="month" selected disabled>
                month
              </option>
              <option value="01">01</option>
              <option value="02">02</option>
              <option value="03">03</option>
              <option value="04">04</option>
              <option value="05">05</option>
              <option value="06">06</option>
              <option value="07">07</option>
              <option value="08">08</option>
              <option value="09">09</option>
              <option value="10">10</option>
              <option value="11">11</option>
              <option value="12">12</option>
            </select>
          </div>
          <div class="inputBox">
            <span>expiration yy</span>
            <select
              name="expyear"
              id=""
              class="year-input"
              required
              value={year}
              onChange={(x) => {
                console.log(x.target.value);
                return setYear(x.target.value);
              }}
            >
              <option value="year" selected disabled>
                year
              </option>
              <option value="2021">2021</option>
              <option value="2022">2022</option>
              <option value="2023">2023</option>
              <option value="2024">2024</option>
              <option value="2025">2025</option>
              <option value="2026">2026</option>
              <option value="2027">2027</option>
              <option value="2028">2028</option>
              <option value="2029">2029</option>
              <option value="2030">2030</option>
            </select>
          </div>
          <div class="inputBox">
            <span>cvv</span>
            <input
              type="text"
              name="cvv"
              maxlength="4"
              class="cvv-input"
              required
              value={cvv}
              onChange={(x) => {
                console.log(x.target.value);
                return setCvv(x.target.value);
              }}
              onMouseEnter={() => {
                document.querySelector(".front").style.transform =
                  "perspective(1000px) rotateY(-180deg)";
                document.querySelector(".back").style.transform =
                  "perspective(1000px) rotateY(0deg)";
              }}
              onMouseLeave={() => {
                document.querySelector(".front").style.transform =
                  "perspective(1000px) rotateY(0deg)";
                document.querySelector(".back").style.transform =
                  "perspective(1000px) rotateY(180deg)";
              }}
            />
          </div>
        </div>
        <input type="submit" value="submit" class="submit-btn" />
      </form>
    </div>
  );
};

export default Payment;
